package com.example.futurefarmjava.Database;import android.content.ContentValues;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;import androidx.annotation.Nullable;public class DbHelper extends SQLiteOpenHelper {    public DbHelper(@Nullable Context context) {        super(context, "UserData.db", null, 1);    }    @Override    public void onCreate(SQLiteDatabase db) {        db.execSQL("create table UserState(_id primary key,UserId TEXT, User_State Integer )");    }    @Override    public void onUpgrade(SQLiteDatabase db, int i, int i1) {        db.execSQL("drop table if exists UserState");    }    public boolean insertUserState (String userId, int userState){        SQLiteDatabase db = this.getWritableDatabase();        ContentValues cv = new ContentValues();        cv.put("UserId",userId);        cv.put("User_State",userState);        long result = db.insert("UserState",null,cv);        if(result == -1){            return false;        }else{            return true;        }    }}